definition.script:
pipeline {
    agent {
        label 'master'
    }
    //environment {
        //NEXUS_CREDENTIAL_ID = credentials('nexus')
    //}
    stages {
        stage('Clone code from Github') {
            steps {
                script {
                    git 'https://github.com/rohit965/LoginWebApp.git'
                }
            }
        }
        stage('Compile and Build') {
            steps {
                script {
                    sh 'mvn clean package'
                }
            }
        }
        stage('Publish Artefact to Nexus Repository Manager') {
            environment {
                NEXUS_VERSION = 'nexus3'
                NEXUS_PROTOCOL = 'http'
                NEXUS_URL = '10.128.0.50:8081'
                NEXUS_REPOSITORY = 'maven-nexus-repo'
                NEXUS_CREDENTIAL_ID = 'nexus-user'
            }
            steps {
                    nexusArtifactUploader(
                        nexusVersion: "${NEXUS_VERSION}",
                            protocol: "${NEXUS_PROTOCOL}",
                            nexusUrl: "${NEXUS_URL}",
                            groupId: 'com.nouryon.apps',
                            version: '1.0-SNAPSHOT',
                            repository: "${NEXUS_REPOSITORY}",
                            credentialsId: "${NEXUS_CREDENTIAL_ID}",
                            artifacts: [
                                [
                                artifactId: 'LoginWebApp',
                                classifier: '',
                                file: 'target/LoginWebApp.war',
                                type: 'war'
                                ],
                                [artifactId: 'pom.xml',
                                classifier: '',
                                file: 'pom.xml',
                                type: 'pom'
                                ]    
                            ]
                    )

            }
        }
}
}
